<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-ts"}}>{`import { Controller } from "./Controller"

interface Options {
    initial?: number
}

interface State extends Options {
    value: number
}

export class SimpleCounterController extends Controller<State> {
    constructor(options: Options = { initial: 0 } as Options) {
        super({
            value: options.initial,
            ...options,
        })
    }

    public increment = () => {
        const { value } = this.state
        this.setState({
            value: value + 1,
        })
    }

    public decrement = () => {
        const { value } = this.state
        this.setState({
            value: value - 1,
        })
    }

    get value() {
        return this.state.value
    }

    get diff() {
        return this.state.initial - this.state
    }
}
`}</MDXTag></MDXTag>
